def pig_latin(w):
    return w[1:] + w[0] + "ay"

def reverse(w):
    return w[::-1]

def identity(w):
    return w


def t(s, transform):
    processing = True
    buffer_ = []
    return_ = []
    transform_lambda = lambda w: w if not w else transform(w)

    for c in s:
        if processing:
            if c == " ":
                w = "".join(buffer_)
                w = transform_lambda(w)
                return_.append(w + " ")
                buffer_ = []
            elif c in "{<>}":
                processing = False

                w = "".join(buffer_)
                w = transform_lambda(w)
                return_.append(w)
                buffer_ = []

                return_.append(c)
            else:
                buffer_.append(c)
        else:
            if c in "{<}>":
                processing = True
            return_.append(c)


    w = "".join(buffer_)
    w = transform_lambda(w)
    return_.append(w)

    return "".join(return_)

def convert_language(transform_func, new_language_code, start_language="en"):
    data = json.load(open(f"../src/langs/json/{start_language}.json", "r"))
    data = {k: {k_: t(v_, transform_func) for k_, v_ in v.items()} for k, v in data.items()}
    json.dump(data, open(f"../src/langs/json/{new_language_code}.json", "w"), indent=2)

convert_language(pig_latin, "piglatin")
convert_language(reverse, "reverse")
convert_language(identity, "es")
for key in target_glyphs:
    mapping = target_glyphs[key]
    def transform_letter(c):
        if c in src_glyphs:
            return mapping[src_glyphs.index(c)]
        return c
    def transform_func(w):
        return ''.join([transform_letter(c) for c in w])

    convert_language(transform_func, key)
